			$("#submitOverlay").fadeOut(500);
			return false;
		});
	});
 
	function positionPopup(){
		if(!$("#submitOverlay").is(':visible')){
			return;
		}
		$("#submitOverlay").css({
			
			left: 200,
			top: 300,
			width:200,
			position:'absolute'
			
		});
	}
 
	$(window).bind('resize',positionPopup);

	function validateQuestions() {
		if ($('#question1').is(':checked') && $('#question2').is(':checked') && $('#question3').is(':checked')) {
		  $('#submitQuestions').submit();
		} else {
			$("#submitOverlay").fadeIn(500);
			positionPopup();
	    return false;
		}
		return false;
	}

